(144, 'busiprof_theme_options', 'a:61:{s:10:"front_page";s:3:"yes";s:12:"upload_image";s:53:"http://armesh.loc/wp-content/uploads/2014/01/logo.png";s:6:"height";s:2:"60";s:5:"width";s:3:"250";s:20:"upload_image_favicon";s:56:"http://armesh.loc/wp-content/uploads/2014/01/favicon.png";s:17:"slider_head_title";s:0:"";s:12:"slider_image";s:57:"http://armesh.loc/wp-content/uploads/2014/01/features.jpg";s:12:"caption_head";s:38:"Cea mai bună plasă o găsiți la noi";s:12:"caption_text";s:97:"Noi oferim calitate înaltă la un preț rezonabil și ne propunem să satisfacem cerințele dvs.";s:19:"service_heading_one";s:10:"Avantajele";s:19:"service_heading_two";s:14:"oferite de noi";s:15:"service_tagline";s:159:"Compania noastră se bazează pe rezultate înalte, oferind o gamă largă de produse de o calitate superioară și o flexibilitate în crearea de noi produse.";s:16:"service_icon_one";s:62:"http://armesh.loc/wp-content/uploads/2014/01/1389477488_34.png";s:16:"service_icon_two";s:62:"http://armesh.loc/wp-content/uploads/2014/01/1389486468_19.png";s:18:"service_icon_three";s:62:"http://armesh.loc/wp-content/uploads/2014/01/1389487788_35.png";s:17:"service_icon_four";s:62:"http://armesh.loc/wp-content/uploads/2014/01/1389488053_27.png";s:17:"service_title_one";s:17:"Calitate Înaltă";s:17:"service_title_two";s:19:"Prețuri rezonabile";s:19:"service_title_three";s:18:"Dimensiuni variate";s:18:"service_title_four";s:16:"Comandare Online";s:16:"service_text_one";s:153:"La fabricarea produselor noastre este folosită materie primă de calitate înaltă și tehnologii inovative care garantează calitatea produsului finit.";s:16:"service_text_two";s:97:"Prețurile produselor noastre sunt calculate după criterii reali, fără adaosuri ”umflate”.";s:18:"service_text_three";s:131:"Noi oferim plasa sudată în baloturi de diferite dimensiuni și grosimi. La solicitarea clienţilor executăm comenzi individuale.";s:17:"service_text_four";s:100:"Prin intermediul siteului nostru puteți comanda produsele noastre online fără nici o dificultate.";s:16:"service_link_btn";s:29:"http://armesh.loc/?page_id=14";s:20:"service_button_value";s:17:"Comandați online";s:19:"busiprof_custom_css";s:0:"";s:24:"home_project_heading_one";s:6:"Recent";s:24:"home_project_heading_two";s:8:"Projects";s:15:"project_tagline";s:114:"We are a group of passionate designers and developers who really love to create awesome wordpress themes & support";s:17:"project_title_one";s:14:"Business Cards";s:17:"project_thumb_one";s:76:"http://armesh.loc/wp-content/themes/busiprof/images/default//rec_project.jpg";s:16:"project_text_one";s:27:"Graphic Design & Web Design";s:15:"project_one_url";s:1:"#";s:17:"project_title_two";s:14:"Business Cards";s:17:"project_thumb_two";s:76:"http://armesh.loc/wp-content/themes/busiprof/images/default//rec_project.jpg";s:16:"project_text_two";s:27:"Graphic Design & Web Design";s:15:"project_two_url";s:1:"#";s:19:"project_title_three";s:14:"Business Cards";s:19:"project_thumb_three";s:76:"http://armesh.loc/wp-content/themes/busiprof/images/default//rec_project.jpg";s:18:"project_text_three";s:27:"Graphic Design & Web Design";s:17:"project_three_url";s:1:"#";s:18:"project_title_four";s:14:"Business Cards";s:18:"project_thumb_four";s:76:"http://armesh.loc/wp-content/themes/busiprof/images/default//rec_project.jpg";s:17:"project_text_four";s:27:"Graphic Design & Web Design";s:16:"project_four_url";s:1:"#";s:18:"testimonials_title";s:12:"Testimonials";s:17:"testimonials_text";s:51:"We are a group of passionate designers & developers";s:22:"testimonials_image_one";s:76:"http://armesh.loc/wp-content/themes/busiprof/images/default//testimonial.jpg";s:21:"testimonials_text_one";s:156:"We are group of passionate designers and developers who really love to create awesome wordpress themes with amazing support & cooles video documentations...";s:21:"testimonials_name_one";s:11:"Hari maliya";s:28:"testimonials_designation_one";s:13:"Web Developer";s:22:"testimonials_image_two";s:76:"http://armesh.loc/wp-content/themes/busiprof/images/default//testimonial.jpg";s:21:"testimonials_text_two";s:156:"We are group of passionate designers and developers who really love to create awesome wordpress themes with amazing support & cooles video documentations...";s:21:"testimonials_name_two";s:10:"Faraz Khan";s:28:"testimonials_designation_two";s:13:"Web Developer";s:17:"recent_blog_title";s:11:"Recent Blog";s:23:"recent_blog_description";s:55:"We are a group of passionate designers &amp; developers";s:25:"busiprof_copy_rights_text";s:36:"&copy; 2013. All Rights Reserved by ";s:17:"footer_designedby";s:7:"webriti";s:10:"footer_url";s:22:"http://www.webriti.com";}', 'yes'),
